(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      console.log('--App Install: --', SELF.name);

      // Fetch repo URL via .zip from appNode.data.staticFileRepo 
      // - extract to appNode's static file directory 
      // - run Docker build command using the input directory as path to run 'npm run second:build' 
      //   WORKDIR=attached-volume/app.second.xyz/directory-of-extracted files (pain in the ass to find?) 

      let doneCanWipe;
      universe.wipeFunc = new Promise(resolve=>{
        doneCanWipe = resolve;
      });

      let {
        req,
        res 
      } = universe.requestsCache[universe.requestId];

      let appPath = universe.navPathv1(SELF.name, 1)
      let appNode = await universe.getNodeAtPath(appPath);

      // RESPONDING IMMEDIATELY
      universe.globalCache.installing = universe.globalCache.installing || {};
      universe.globalCache.installing[appPath] = universe.globalCache.installing[appPath] || {
        started: false,
        error: null,
      }
      let installing = universe.globalCache.installing[appPath];
      if(installing.started){
        if(installing.error){
          // send error once 
          res.send({
            error: 'Error in previous, now can run /install again',
            message: installing.error,
            lastMessage: installing.lastMessage
          });
          installing.started = true;
          installing.error = false;
          return;
        } else {
          res.send({
            installing: true,
            started: installing.started,
            message: installing.lastMessage
          });
          return;
        }
      } else {
        res.send('Install started.');
        installing.started = Date.now();
        installing.error = null;
        installing.lastMessage = 'Starting';
      }

      // console.log('Writing file:', finalVolumeLookupPath, path.dirname(finalVolumeLookupPath));
      // mkdirp.sync(path.dirname(finalVolumeLookupPath));
      // fs.writeFileSync(finalVolumeLookupPath, 'testingafile');

      const path = universe.require('path');
      const mkdirp = universe.require('mkdirp');

      let fs = universe.require('fs-extra');
      let request = universe.require('request');
      let unzipper = universe.require('unzipper');


      let volumePrefix = universe.env.ATTACHED_VOLUME_ROOT + appPath;

      let tmppath = universe.env.ATTACHED_VOLUME_ROOT + 'app.second.xyz2';//appPath;
      mkdirp.sync(tmppath);
      // let staticRootPath = volumePrefix + staticFileDirectory;

      // clear directory 
      // - shouldn't need to clear node_modules every time? 
      fs.emptyDirSync(volumePrefix)

      let zipUrl = appNode.data.frontendBuildDataZipUrl;

      // fetch zip and extract to correct place 
      console.log('Extracting');
      installing.lastMessage = 'Extracting';
      let Writer = universe.require('fstream').Writer;
      let removeFirstLevelDirectory = (zipUrl.indexOf('github') > -1 ) ? true:false; // necessary for importing zip from github 
      // request('https://github.com/secondai/second_template_pwa/archive/master.zip')
      // request('https://repl.it/@nicholasareed/secondtemplatepwa.zip')
      await request(zipUrl)
      .pipe(unzipper.Extract({ 
        path: path.resolve(volumePrefix),
        getWriter: ({path})=>{
          // rewrite paths to extract to SAME DIRECTORY 
          // - fixes github zip, might break other sites 
          if(removeFirstLevelDirectory){
            path = volumePrefix + '/' + path.substring(volumePrefix.length).split('/').slice(2).join('/');
          }
          console.log('updated path', path);
          return Writer({path})
        }
      }))
      .promise();

      ////////////////////////////////////////////////////////////////////////
      //////////////////////////////////////////////////////////////////////// EXITING HERE
      ////////////////////////////////////////////////////////////////////////
      installing.started = false;
      res.send('extracted, NOT installed');
      return false;

      // run build commands
      // 
      console.log('------Running Build Commands-----');

      installing.lastMessage = 'Running build commands';

      const { exec } = universe.require('child_process');

      // let command = `docker exec builder /bin/bash -c "cd /attached-volume && ls -al && cd app.second.sample_pwa && ls -al && pwd"`;

      // things to do:
      // - clear node_modules 
      // - install package.json 
      // - run build command 

      let command = `docker exec builder /bin/bash -c "cd ${volumePrefix} && npm i && npm run second:build"`;
      console.log('------Executing Command-------');

      installing.lastMessage = 'Executing command: ' + command;
      console.log('Command:', command);
      exec(command, (err, stdout, stderr) => {
        console.log('----------Returned from Command------------');

        if (err) {
          console.error('------------------------------');
          console.error(`---exec error: ${err}`);
          console.error('------------------------------');
          installing.error = true;
          installing.lastMessage = `stderr: ${stderr}.  STDOUT: ${stdout}`;
          doneCanWipe()
          resolve(false);
          return;
        }

        console.log('------------------------------');
        console.log(`---exec Run Output: ${stdout}`);
        console.log('------------------------------');
        installing.error = false;
        installing.lastMessage = `Complete`;
        doneCanWipe()
        resolve(true);
      });


    }catch(err){
      universe.scriptError(err, SELF);
      resolve({ERROR: true, err: err.toString()});
    }


  })
})()
