(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      console.log('--Service --', SELF.name, '--');

      const fs = universe.require('fs-extra');
      const path = universe.require('path');

      let input = INPUT.data;

      let result = fs.writeFileSync(path.join(universe.env.ATTACHED_VOLUME_ROOT, PATH, input.path), input.text, 'utf8');

      return resolve({
        type: 'types.second.default.fs.put_text.response',
        data: result
      });

    }catch(err){
      universe.scriptError(err, SELF);
      resolve({ERROR: true, err: err.toString()});
    }


  })
})()
